import React from "react";
import axios from "axios";
import Autocomplete from "./AutoComplete.jsx";

import Autosuggest from 'react-autosuggest';

// Imagine you have a list of languages that you'd like to autosuggest.


const languages = [
  {
    name: 'C',
    year: 1972
  },
  {
    name: 'Elm',
    year: 2012
  }
];
console.log(languages+"sdf")
// Teach Autosuggest how to calculate suggestions for any given input value.
const getSuggestions = value => {
  const inputValue = value.trim().toLowerCase();
  const inputLength = inputValue.length;

  return inputLength === 0 ? [] : languages.filter(lang =>
    lang.name.toLowerCase().slice(0, inputLength) === inputValue
  );
};

// When suggestion is clicked, Autosuggest needs to populate the input
// based on the clicked suggestion. Teach Autosuggest how to calculate the
// input value for every given suggestion.
const getSuggestionValue = suggestion => suggestion.name;

// Use your imagination to render suggestions.
const renderSuggestion = suggestion => (
  <div>
    {suggestion.name}
  </div>
);

export default class MachinePartyRegistration extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      date: null,

      partyList: [],
      machineList: [],

      selectedMachine: null,
      selectedParty: null,

      partyExistMessage: [],

      value: '',
      suggestions: [],

      remark: null,
      dieselamount: null,
      drillingFeet: null,

      buttonStatus: {
        visibility: "visible"
      },
      radioButtonStyle: {
        float: "left"
      }
    };

    // Fetch party list from server
    this.state.fetchProduct = async () => {
      const responsepartyList = await fetch(
        "http://127.0.0.1:8000/list-of-machineparty/"
      );
      const jsonpartyList = await responsepartyList.json();
      this.state.partyList = jsonpartyList;

      const responsemachineList = await fetch(
        "http://127.0.0.1:8000/list-of-machines/"
      );
      const jsonmachineList = await responsemachineList.json();
      this.state.machineList = jsonmachineList;
    };

    this.state.fetchProduct();

    // Check existence of party name
    this.state.checkparty = () => {
      try {
        this.setState({
          partyExistMessage: [],
          buttonStatus: {
            visibility: "visible"
          }
        });

        // this.state.partyExistMessage = [];
        const showList = (item, index) => {
          if (
            item.name
              .toLowerCase()
              .startsWith(this.state.partyName.toLowerCase())
          ) {
            this.state.partyExistMessage.push(item.name.toLowerCase());
            //this.state.partyExistMessage=item.name.toLowerCase();

            this.setState({
              partyExistMessage: this.state.partyExistMessage,
              buttonStatus: {
                visibility: "hidden"
              }
            });
          } else {
          }
        };
        this.state.partyList.forEach(showList);
      } catch (err) {}
    };

    this.state.onSubmit = e => {
      //   axios
      //     .post("", {
      //       name: this.state.partyName,
      //       contact: this.state.partyContact,
      //       village: this.state.partyVillage
      //     })
      //     .then(res => {
      //       this.state.fetchProduct();
      //     })
      //     .catch(error => {
      //       alert(error.response.request._response);
      //     });
      console.log(this.state.date);
      console.log(this.state.dieselamount);
      console.log(this.state.drillingFeet);
      console.log(this.state.remark);
      console.log(this.state.selectedParty);
      console.log(this.state.selectedMachine);

      e.target.reset();
      e.preventDefault();
    };

    this.state.getDate = () => {
      // var date = { currentTime: new Date().toLocaleString() }
      var curr = new Date();
      curr.setDate(curr.getDate() + 3);
      var date = curr.toISOString().substr(0, 10);
      this.state.date = date;
    };

    this.state.getDate();
  }
  onChange = (event, { newValue }) => {
    this.setState({
      value: newValue
    });
  };

  // Autosuggest will call this function every time you need to update suggestions.
  // You already implemented this logic above, so just use it.
  onSuggestionsFetchRequested = ({ value }) => {
    this.setState({
      suggestions: getSuggestions(value)
    });
  };

  // Autosuggest will call this function every time you need to clear suggestions.
  onSuggestionsClearRequested = () => {
    this.setState({
      suggestions: []
    });
  };

  render() {


    const { value, suggestions } = this.state;

    // Autosuggest will pass through all these props to the input.
    const inputProps = {
      placeholder: 'Type a programming language',
      value,
      onChange: this.onChange
    };

    return (
      <form
        className="form-container form-group"
        onSubmit={e => this.state.onSubmit(e)}
      >
        <p className="headingViewPart">Machine Party Registration</p>
        <div className="pt-5">
          <input
            type="text"
            className="mb-2"
            name="partyName"
            placeholder="Party Name"
            autoComplete="off"
            maxLength="30"
            minLength="5"
            onChange={e => {
              this.state.partyName = e.target.value;
              this.state.checkparty();
            }}
            required
          />

          {/* <p>{this.state.partyExistMessage}</p> */}
          <div>
            {this.state.partyExistMessage.map(v => (
              <p key={v}>{v}</p>
            ))}
          </div>
          <br />

          <input
            type="date"
            //data-date=""
            data-date-format="YYYY-MM-DD"
            defaultValue={this.state.date}
            name="date"
            onChange={e => {
              this.state.date = e.target.value;
            }}
            required
          ></input>
          <input
            type="text"
            className="mb-2"
            name="remark"
            placeholder="Remark"
            autoComplete="off"
            maxLength="30"
            //minLength="5"
            onChange={e => {
              this.state.remark = e.target.value;
            }}
            //required
          />
          <input
            type="number"
            className="mb-2"
            name="dieselAmount"
            placeholder="Diesel Amount"
            autoComplete="off"
            onChange={e => {
              this.state.dieselamount = e.target.value;
            }}
            required
          />
          <input
            type="number"
            className="mb-2"
            name="drillingFeet"
            placeholder="Drilling Feet"
            autoComplete="off"
            onChange={e => {
              this.state.drillingFeet = e.target.value;
            }}
            required
          />
          <Autocomplete suggestions={this.state.partyList} />
          <Autosuggest
        suggestions={suggestions}
        onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
        onSuggestionsClearRequested={this.onSuggestionsClearRequested}
        getSuggestionValue={getSuggestionValue}
        renderSuggestion={renderSuggestion}
        inputProps={inputProps}
      />
        </div>
        <button
          type="submit"
          className="btn btn-outline-dark"
          style={this.state.buttonStatus}
        >
          Save
        </button>
      </form>
    );
  }
}
