import React, { Fragment } from "react";

export default class InputDateField extends React.Component {
  constructor() {
    super();

    var today = new Date(),
      date =
        today.getFullYear() +
        "-" +
        (today.getMonth() + 1) +
        "-" +
        today.getDate();

    this.state = {
      date: date
    };
  }

  render() {
    return (
      <Fragment>
        <input
          type="date"
          data-date-format="YYYY-MM-DD"
          defaultValue={this.state.date}
          name="date"
          onChange={this.state.onChange()}
          required
        />
      </Fragment>
    );
  }
}
