import React from "react";
import axios from "axios";

export default class MachineWorkEntry extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      partyName: "",
      //partyContact: "",
      //partyVillage: "",
      remark: "",
      workDate: 0,
      //partyId: 0,
      drillingFt: 0,
      dieselAmount: 0,
      tech: "select",

      partyList: {},
      machineList: [],

      partyExistMessage: "",
      buttonStatus: {
        visibility: "visible"
      },
      radioButtonStyle: {
        float: "left"
      }
    };

    // Fetch party list from server
    this.state.fetchProduct = async () => {
      const responsepartyList = await fetch(
        "http://127.0.0.1:8000/list-of-machineparty/"
      );
      const jsonpartyList = await responsepartyList.json();
      this.state.partyList = jsonpartyList;

      const responsemachineList = await fetch(
        "http://127.0.0.1:8000/list-of-machines/"
      );
      const jsonmachineList = await responsemachineList.json();
      this.state.machineList = jsonmachineList;
      console.log(this.state.machineList);
    };

    this.state.fetchProduct();

    console.log(this.state.machineList);
    console.log(this.state.partyList);
    console.log(this.state.tech);
    // Check existence of party name
    this.state.checkparty = () => {
      try {
        this.setState({
          partyExistMessage: "",
          buttonStatus: {
            visibility: "visible"
          }
        });
        const showList = (item, index) => {
          if (this.state.partyName.toLowerCase() === item.name.toLowerCase()) {
            this.setState({
              partyExistMessage: "* This party name is already exist!!!",
              buttonStatus: {
                visibility: "hidden"
              }
            });
          } else {
          }
        };
        this.state.partyList.forEach(showList);
      } catch (err) {}
    };

    this.state.onSubmit = e => {
      axios
        .post("http://127.0.0.1:8000/machine-party-registration/", {
          name: this.state.partyName,
          contact: this.state.partyContact,
          village: this.state.partyVillage
        })
        .then(res => {
          this.state.fetchProduct();
        })
        .catch(error => {
          alert(error.response.request._response);
        });
      e.target.reset();
      e.preventDefault();
    };
  }

//   handleChange(e) {
//     this.setState({
//       tech: e.target.value
//     });
//     this.state.tech = e.target.value;
//     console.log(this.state.tech);
//   }
  handleChange = event => {
    this.state.tech= event.target.value;
    this.props.onSelectChange(this.state.tech);
  };

  render() {
    return (
      <form
        className="form-container form-group"
        onSubmit={e => this.state.onSubmit(e)}
      >
        <p className="headingViewPart">Machine Work Registration</p>
        <div className="pt-5">
          <input
            type="text"
            className="mb-2"
            name="machineName"
            placeholder="Machine Name"
            autoComplete="off"
            maxLength="30"
            minLength="5"
            onChange={e => {
              this.state.partyName = e.target.value;
              this.state.checkparty();
            }}
            required
          />

          <p>{this.state.partyExistMessage}</p>
          <br />

          {/* <select
            onChange={this.handleChange.bind(this)}
            value={this.state.tech}
          >
            <option value="select">Select a technology</option>
            <option value="Angular">Angular</option>
            <option value="Bootstrap">Bootstrap</option>
            <option value="React">React</option>
          </select> */}
          <select
            name="customSearch"
            className="custom-search-select"
            onChange={this.handleChange}
          >
            <option>Select Item</option>
            {this.state.machineList.map((option, i) => (
              <option
                // key={data.name}
                value={option}
              >
                {option}
              </option>
            ))}
          </select>
          <br />
          <br />
        </div>
        <button
          type="submit"
          className="btn btn-outline-dark"
          style={this.state.buttonStatus}
        >
          Save
        </button>
      </form>
    );
  }
}
