import React from "react";
import axios from "axios";
import InputPartyNameField from "../modular/InputPartyNameField";
import InputDateField from "../modular/InputDateField";
import InputContactField from "../modular/InputContactField";

export default class DailyWorkEntry extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      partyName: "",
      partyContact: "",
      partyVillage: "",
      date: null,
      fiveFeet: 0,
      fiveFeetRate: 0,
      twoHalfFeet: 0,
      twoHalfFeetRate: 0,
      dieselSpend: 0,
      responseMessage: "",
      buttonStatus: {
        visibility: "visible"
      }
    };

    this.state.onChange = () => {
      this.setState({
        workerExistMessage: "",
        responseMessage: ""
      });
    };

    //Form Handler
    this.state.onSubmit = e => {
      axios
        .post("http://127.0.0.1:8000/enter-daily-work/", {
          name: this.state.partyName,
          contact: this.state.partyContact,
          village: this.state.partyVillage,
          date: this.state.date,
          five_feet: this.state.fiveFeet,
          five_feet_rate: this.state.fiveFeetRate,
          two_half_feet: this.state.twoHalfFeet,
          two_half_feet_rate: this.state.twoHalfFeetRate,
          diesel_spend: this.state.dieselSpend
        })
        .then(res => {
          this.setState({
            responseMessage: res.data
          });
        })
        .catch(error => {
          console.log(error.response.request._response);
        });

      e.target.reset();
      e.preventDefault();
    };
  }

  render() {
    return (
      <form
        className="form-container form-group"
        onSubmit={e => this.state.onSubmit(e)}
      >
        <p className="headingViewPart">Daily Work Entry</p>
        <div className="pt-5">
          <InputPartyNameField
            callbackFromParent={dataFromChild => {
              this.state.partyName = dataFromChild;
            }}
            checkFromParent={e => {}}
          />

          <p>{this.state.workerExistMessage}</p>
          <br />

          <InputContactField
            callbackFromParent={dataFromChild => {
              this.state.partyContact = dataFromChild;
            }}
          />

          <br />
          <br />

          <input
            type="text"
            className="mb-2"
            name="partyVillage"
            placeholder="Village"
            autoComplete="off"
            maxLength="30"
            minLength="5"
            onChange={e => {
              this.state.partyVillage = e.target.value;
              this.state.onChange();
            }}
            required
          />

          <br />
          <br />

          <InputDateField
            callbackFromParent={dataFromChild => {
              this.state.date = dataFromChild;
            }}
          />
          <br />
          <br />

          <input
            type="number"
            className="mb-2"
            name="fiveFeet"
            placeholder="5 Feet"
            autoComplete="off"
            onChange={e => {
              this.state.fiveFeet = e.target.value;
              this.state.onChange();
            }}
            required
          />

          <br />
          <br />

          <input
            type="number"
            step="0.1"
            className="mb-2"
            name="fiveFeetRate"
            placeholder="5 Feet Rate"
            autoComplete="off"
            onChange={e => {
              this.state.fiveFeetRate = e.target.value;
              this.state.onChange();
            }}
            required
          />

          <br />
          <br />

          <input
            type="number"
            className="mb-2"
            name="twoHalfFeet"
            placeholder="2.5 Feet"
            autoComplete="off"
            onChange={e => {
              this.state.twoHalfFeet = e.target.value;
              this.state.onChange();
            }}
            required
          />

          <br />
          <br />

          <input
            type="number"
            step="0.1"
            className="mb-2"
            name="twoHalfFeetRate"
            placeholder="2.5 Feet Rate"
            autoComplete="off"
            onChange={e => {
              this.state.twoHalfFeetRate = e.target.value;
              this.state.onChange();
            }}
            required
          />

          <br />
          <br />

          <input
            type="number"
            className="mb-2"
            name="dieselSpend"
            placeholder="Diesel Spend"
            autoComplete="off"
            onChange={e => {
              this.state.dieselSpend = e.target.value;
              this.state.onChange();
            }}
            required
          />
        </div>
        <p>{this.state.responseMessage}</p>
        <button
          type="submit"
          className="btn btn-outline-dark"
          style={this.state.buttonStatus}
        >
          Save
        </button>
      </form>
    );
  }
}
