import React, { Component, Fragment } from "react";
import PropTypes from "prop-types";

export default class InputContactField extends Component {
  constructor(props) {
    super(props);

    this.state = {
      contactVal : 0
    };
  }

  onChange = e => {
   var numm= e.target.value.replace(/[^0-9]/g, '')
   this.state.contactVal=numm
    this.props.callbackFromParent(numm);
  };

  render() {
    return (
      <Fragment>

        <input
            type="text"
            className="mb-2"
            //name="partyContact"
            //pattern="^\d{10}$"
            pattern="\d*"
            value={this.state.contactVal}
            maxLength="10"
            placeholder="Enter 10 digit Phone no"
            autoComplete="off"
            onChange={this.onChange}
            required
          />
      </Fragment>
    );
  }
}
