import React from "react";
import axios from "axios";
import InputContactField from "../modular/InputContactField";
import InputDateField from "../modular/InputDateField";
import InputCommonName from "../modular/InputCommonName";
export default class WorkerRegistration extends React.Component {
  // Fetch vehicle list from server
  fetchProduct = async () => {
    try {
      const responseWorkerList = await fetch(
        "http://127.0.0.1:8000/list-of-worker/"
      );
      const jsonWorkerList = await responseWorkerList.json();
      this.state.workerList = jsonWorkerList;
    } catch {
      this.toggleLoadStatus();
    }
  };

  // Check existence of vehicle name
  checkWorker = () => {
    try {
      this.setState({
        workerExistMessage: "",
        responseMessage: "",
        buttonStatus: {
          visibility: "visible"
        }
      });
      const showList = (item, index) => {
        if (this.state.workerName.toLowerCase() === item.name.toLowerCase()) {
          this.setState({
            workerExistMessage: "* This worker name is already exist!!!",
            buttonStatus: {
              visibility: "hidden"
            }
          });
        } else {
        }
      };
      this.state.workerList.forEach(showList);
    } catch (err) {}
  };

  onSubmit = e => {
    axios
      .post("http://127.0.0.1:8000/worker-registration/", {
        name: this.state.workerName,
        contact: this.state.workerContact,
        village: this.state.workerVillage,
        salary: this.state.workerSalary,
        advance: this.state.advance,
        date: this.state.date
      })
      .then(res => {
        this.state.fetchProduct();
        this.setState({
          responseMessage: res.data
        });
      })
      .catch(error => {
        console.log(error.response.request._response);
      });

    e.target.reset();
    e.preventDefault();
  };

  // toggle load status
  toggleLoadStatus = async () => {
    if (this.state.loadingStatus.visibility === "visible") {
      await this.setState({
        loadingStatus: {
          visibility: "hidden"
        },
        loadedStatus: {
          visibility: "visible"
        }
      });
    } else {
      await this.setState({
        loadingStatus: {
          visibility: "visible"
        },
        loadedStatus: {
          visibility: "hidden"
        }
      });
    }
  };

  constructor(props) {
    super(props);

    this.state = {
      workerName: "",
      workerContact: "",
      workerVillage: "",
      workerSalary: 0,
      advance: 0,
      date: null,
      workerList: {},
      workerExistMessage: "",
      responseMessage: "",
      buttonStatus: {
        visibility: "visible"
      },
      loadingStatus: {
        visibility: "visible"
      },
      loadedStatus: {
        visibility: "hidden"
      }
    };

    this.fetchProduct = this.fetchProduct.bind(this);
    this.checkWorker = this.checkWorker.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.toggleLoadStatus = this.toggleLoadStatus.bind(this);
    this.fetchProduct();
  }

  componentDidMount() {
    this.toggleLoadStatus();
  }

  render() {
    return (
      <form
        className="form-container form-group"
        onSubmit={e => this.state.onSubmit(e)}
      >
        <p className="headingViewPart">Machine Worker Registration</p>
        <div className="pt-5">
          <InputCommonName
            minLengthh={3}
            placeholderParent={"Worker Name"}
            callbackFromParent={dataFromChild => {
              this.state.workerName = dataFromChild;
            }}
            checkFromParent={this.state.checkWorker}
          />

          <p>{this.state.workerExistMessage}</p>
          <br />

          <InputContactField
            callbackFromParent={dataFromChild => {
              this.state.workerContact = dataFromChild;
            }}
          />

          <br />
          <br />

          <input
            type="text"
            className="mb-2"
            name="workerVillage"
            placeholder="Worker Village"
            autoComplete="off"
            maxLength="30"
            minLength="5"
            onChange={e => {
              this.state.workerVillage = e.target.value;
            }}
            required
          />

          <br />
          <br />

          <input
            type="number"
            className="mb-2"
            name="workerSalary"
            placeholder="Worker Salary"
            autoComplete="off"
            onChange={e => {
              this.state.workerSalary = parseInt(e.target.value);
            }}
            required
          />

          <br />
          <br />

          <InputDateField
            callbackFromParent={dataFromChild => {
              this.state.date = dataFromChild;
            }}
          />

          <br />
          <br />

          <input
            type="number"
            className="mb-2"
            name="workerAdvance"
            placeholder="Worker Advance"
            autoComplete="off"
            onChange={e => {
              this.state.advance = parseInt(e.target.value);
            }}
            required
          />
        </div>
        <p>{this.state.responseMessage}</p>
        <button
          type="submit"
          className="btn btn-outline-dark"
          style={this.state.buttonStatus}
        >
          Save
        </button>
      </form>
    );
  }
}
