import React from "react";
import axios from "axios";
import Autocomplete from "./AutoComplete.jsx";

const partyNamesFromApi = [];
const machineNamesFromApi = [];

fetch("http://127.0.0.1:8000/list-of-machineparty/")
  .then(res => res.json())
  .then(out => {
    partyListFunction(out);
  })
  .catch(err => {
    throw err;
  });

fetch("http://127.0.0.1:8000/list-of-machines/")
  .then(res => res.json())
  .then(out => {
    machineListFunction(out);
  })
  .catch(err => {
    throw err;
  });
//below function is used to store api data in a array
function partyListFunction(data) {
  data.map(item => partyNamesFromApi.push(item.name));
  console.log("partyNameFromApi", partyNamesFromApi);
}
function machineListFunction(data) {
  data.map(item => machineNamesFromApi.push(item.name));
  console.log("machineNameFromApi", machineNamesFromApi);
}

export default class MachinePartyRegistration extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      date: null,

      partyList: [],
      machineList: [],

      selectedMachine: null,
      selectedParty: null,

      partyExistMessage: "",

      remark: null,
      dieselamount: null,
      drillingFeet: null,

      buttonStatus: {
        visibility: "visible"
      },
      radioButtonStyle: {
        float: "left"
      }
    };

    // Fetch party list from server
    this.state.fetchProduct = async () => {
      const responsepartyList = await fetch(
        "http://127.0.0.1:8000/list-of-machineparty/"
      );
      const jsonpartyList = await responsepartyList.json();
      this.state.partyList = jsonpartyList;

      const responsemachineList = await fetch(
        "http://127.0.0.1:8000/list-of-machines/"
      );
      const jsonmachineList = await responsemachineList.json();
      this.state.machineList = jsonmachineList;
    };

    this.state.fetchProduct();

    // Check existence of party name
    this.state.checkparty = dataFromChild => {
        console.log("didwana");

        try {
          this.setState({
               partyExistMessage :"",
               buttonStatus: {
                   visibility: 'hidden'
               }
              });
          const showList = (item, index) => {
              if (dataFromChild.toLowerCase() === item.name.toLowerCase()){
                  console.log("ashok");

                this.setState({
                   //partyExistMessage :"* This party name is already exist!!!",
                      buttonStatus: {
                    visibility: 'visible'
                   }
                });
              }
              else{}
          };
          this.state.partyList.forEach(showList);
        }
        catch (err) {}
      }

    this.state.onSubmit = e => {
      //   axios
      //     .post("", {
      //       name: this.state.partyName,
      //       contact: this.state.partyContact,
      //       village: this.state.partyVillage
      //     })
      //     .then(res => {
      //       this.state.fetchProduct();
      //     })
      //     .catch(error => {
      //       alert(error.response.request._response);
      //     });
      console.log(this.state.date);
      console.log(this.state.dieselamount);
      console.log(this.state.drillingFeet);
      console.log(this.state.remark);
      console.log(this.state.selectedParty);
      console.log(this.state.selectedMachine);

      e.target.reset();
      e.preventDefault();
    };

    this.state.getDate = () => {
      var curr = new Date();
      curr.setDate(curr.getDate() );
      var date = curr.toISOString().substr(0, 10);
      this.state.date = date;
    };

    this.state.getDate();
  }

  myCallbackForSelectedParty = dataFromChild => {
    this.state.selectedParty = dataFromChild;
  };
  myCallbackForSelectedMachine = dataFromChild => {
    this.state.selectedMachine = dataFromChild;
  };

  render() {
    return (
      <form
        className="form-container form-group"
        onSubmit={e => this.state.onSubmit(e)}
      >
        <p className="headingViewPart">Machine Party Registration</p>
        <div className="pt-5">
          <Autocomplete
            suggestions={partyNamesFromApi}
            callbackFromParent={this.myCallbackForSelectedParty}
            checkPartyFromParent={this.state.checkparty}
            placeholderfrom={"enter Party name"}
          />
          <p>{this.state.partyExistMessage}</p>
          <Autocomplete
            suggestions={machineNamesFromApi}
            callbackFromParent={this.myCallbackForSelectedMachine}
            placeholderfrom={"enter Machine name"}
          />
          <input
            type="date"
            //data-date=""
            data-date-format="YYYY-MM-DD"
            defaultValue={this.state.date}
            name="date"
            onChange={e => {
              this.state.date = e.target.value;
            }}
            required
          />
          <input
            type="text"
            className="mb-2"
            name="remark"
            placeholder="Remark"
            autoComplete="off"
            maxLength="30"
            //minLength="5"
            onChange={e => {
              this.state.remark = e.target.value;
            }}
            //required
          />
          <input
            type="number"
            className="mb-2"
            name="dieselAmount"
            placeholder="Diesel Amount"
            autoComplete="off"
            onChange={e => {
              this.state.dieselamount = e.target.value;
            }}
            required
          />
          <input
            type="number"
            className="mb-2"
            name="drillingFeet"
            placeholder="Drilling Feet"
            autoComplete="off"
            onChange={e => {
              this.state.drillingFeet = e.target.value;
            }}
            required
          />
        </div>
        <button
          type="submit"
          className="btn btn-outline-dark"
          style={this.state.buttonStatus}
        >
          Save
        </button>
      </form>
    );
  }
}
