import React from 'react';
import Autocomplete from "../entry/AutoComplete";

class MachineCredit extends React.Component{
    // Fetch machine list from server
    fetchProduct = async () => {
        try {
        const responsePartyList = await fetch(
            "http://127.0.0.1:8000/list-of-machineparty/" 
        );
        const jsonPartyList = await responsePartyList.json();
        jsonPartyList.map(item => this.state.partyNamesFromApi.push(item.name));
        this.state.partyList = jsonPartyList;
        } catch {
        }
    };

  //form Handler Submitting
  onSubmit = e => {
    axios
      .post("http://127.0.0.1:8000/enter-machineparty-work/", {
        party: this.state.selectedParty,
        machine: this.state.selectedMachine,
        date: this.state.date,
        drilling_feet: this.state.drillingFeet,
        diesel_amount: this.state.dieselAmount,
        remark: this.state.remark,
        payment: this.state.payment,
        holes: this.state.holes
      })
      .then(res => {
        this.fetchProduct();
        this.setState({
          responseMessage: res.data
        });
      })
      .catch(error => {
        alert(error.response.request._response);
      });

    e.target.reset();
    e.preventDefault();
  };

    constructor(props){
        super(props);
        this.state= {
            partyList: {},
            selectedParty: "",
            inputOrTable: {
                visibility: "hidden"
            },
            partyNamesFromApi: []
        }

        this.fetchProduct= this.fetchProduct.bind(this);
        this.fetchProduct();
    }
    render(){
        return(
            <form
            className="form-container form-group"
            onSubmit={e => this.onSubmit(e)}
            >
                <div className="" style={this.state.inputOrTable}>
                    <h3>Riddhesh Jangid</h3>
                </div>
                <Autocomplete
                    suggestions={this.state.partyNamesFromApi}
                    callbackFromParent={dataFromChild => {
                    this.state.selectedParty = dataFromChild;
                    }}
                    placeholderfrom={"Party name"}
                />
                <br />
                <br />
                <button
                    type="submit"
                    className="btn btn-outline-dark"
                    style={this.state.buttonStatus}>
                Save
                </button>
            </form>
        );
    }
}

export default MachineCredit;
