import React, { useState, useEffect } from "react";
import axios from "axios";
import { useForm } from "react-hook-form";

export default function MachineRegistration() {
  const [data, setData] = useState({});
  const [machinename, userInput] = useState(""); 

  useEffect(() => {
    async function fetchProduct() {
      const response = await fetch("http://127.0.0.1:8000/list-of-machines/");

      const res = await response.json();
      setData(res);
    }

    fetchProduct();
    checkMachine();
  }, [machinename]);

  console.log(machinename);

  // below function is used to check typed machine name is already in database or not
  function checkMachine() {
    try {
      const showList = (item, index) => {
        if (machinename === item.name) {

          alert("Chng machine name");
          {errors.value="enter diff name"}
          userInput('')
        }
      };
      data.forEach(showList);
    } catch (err) {}
  }

  const { register, errors, handleSubmit } = useForm();
  const onSubmit = data => {
    alert(JSON.stringify(data));
    axios
      .post(`http://127.0.0.1:8000/machine-registration/`, {
        name: machinename
      })
      .then(res => {
        console.log(res);
        console.log(res.data);
        setData(res);
      });
  };
  console.log(errors);

  return (
    <div>
      <center>
        <h2>Machine Registration</h2>
      </center>
      <div className="">
        <form onSubmit={handleSubmit(onSubmit)}>
          <div>
            <label>First name</label>
            <input
              type="text"
              name="First name"
              ref={register({ required: true, maxLength: 80 })}
              onChange={e => userInput(e.target.value)}
            />
            {errors.value}

          </div>

          <input type="submit" />
        </form>
      </div>
    </div>
  );
}
