import React, { useState, useEffect } from "react";
import axios from "axios";
import { useForm } from "react-hook-form";
import InputField from '../modular/InputField';

export default class Machinereg extends React.Component{
  constructor(){
    this.state= {
      machineName: "",
      machineList: {},
      errorMessage: "",
    }    

    this.handleSubmit = this.handleSubmit.bind(this)

    const fetchProduct = async () =>{
      console.log("Fetch calls");
      const responseMachineList = await fetch("http://127.0.0.1:8000/list-of-machines/");
      const jsonMachineList = await responseMachineList.json();
      this.state.machineList=jsonMachineList;
    }

    fetchProduct(); 

    function checkMachine() {
      try {
        //errors.firstName.message();
        this.state.errorMessage="";
        const showList = (item, index) => {
          if (this.state.machineName === item.name) {
            this.state.errorMessage ="* This machine name is already exist!!!";
          }
          else{}
        };
        this.state.machineList.forEach(showList);
      } 
      catch (err) {}
    }

    const { register, errors, handleSubmit, reset } = useForm();

    this.onSubmit = (data, e) => {
      alert("machine registered successfully"+this.state.machineName);
      axios.post(`http://127.0.0.1:8000/machine-registration/`, {
          name: this.state.machineName
        })
        .then(res => {
          console.log(res);
          console.log(res.data);
          //setMachineList(res);
        });
      e.target.reset();
    };
  }

  render(){
    return (
      <form className="form-container form-group" onSubmit={this.handleSubmit(onSubmit)}>
      <p className="headingViewPart">Machine Registration</p>
      <div className="pt-5">
      <input 
            type="text" 
            className="mb-2" 
            name="machineName" 
            ref={register({
              required: true,
              maxLength: 30,
              minLength: { value: 5, message: "Min length is 5" }
            })}
            placeholder="Machine Name" 
            onChange={ e => this.state.machineName=e.target.value } 
      />
      </div>
      <br/>
      <button type="submit" className="btn btn-outline-dark">Save</button>
      </form> 
    );
  }

}



function MachineRegistration() {
  const [machineName, setMachineName] = useState("");
  const [machineList, setMachineList] = useState({});
  const [errorMessage, setErrorMessage] = useState("");

  const fetchProduct = async () =>{
      console.log("Fetch calls");
      const responseMachineList = await fetch("http://127.0.0.1:8000/list-of-machines/");
      const jsonMachineList = await responseMachineList.json();
      setMachineList(jsonMachineList);
    }

    fetchProduct(); 

    function checkMachine() {
      try {
        //errors.firstName.message();
        setErrorMessage("");
        const showList = (item, index) => {
          if (machineName === item.name) {
            setErrorMessage("* This machine name is already exist!!!");
          }
          else{}
        };
        machineList.forEach(showList);
      } 
      catch (err) {}
    }

    useEffect(
      () => {
        checkMachine();
      },
      [machineList]
    );

  const { register, errors, handleSubmit, reset } = useForm();

  const onSubmit = (data, e) => {
    alert("machine registered successfully"+machineName);
    axios.post(`http://127.0.0.1:8000/machine-registration/`, {
        name: machineName
      })
      .then(res => {
        console.log(res);
        console.log(res.data);
        //setMachineList(res);
      });
    e.target.reset();
  };
  console.log(errors);

  return (
							<form className="form-container form-group" onSubmit={handleSubmit(onSubmit)}>
							<p className="headingViewPart">Machine Registration</p>
							<div className="pt-5">
              <input 
                    type="text" 
                    className="mb-2" 
                    name="machineName" 
                    ref={register({
                      required: true,
                      maxLength: 30,
                      minLength: { value: 5, message: "Min length is 5" }
                    })}
                    placeholder="Machine Name" 
                    onChange={e => setMachineName(e.target.value)} 
              />
							</div>
              <br/>
							<button type="submit" className="btn btn-outline-dark">Save</button>
							</form>      
  );
}

