import PropTypes from "prop-types";

export default class InputContactField extends Component {
  constructor(props) {
    super(props);

    this.state = {

    };
  }

  onChange = e => {
   var numm= e.target.value.replace(/[^0-9]/g, '')

    this.props.callbackFromParent(numm);
  };

  render() {
    return (
      <Fragment>

        <input
            type="tel"
            className="mb-2"

            //pattern="/^-?\d+\.?\d*$/" 
            //onKeyPress="if(e.target.value.length==10) return false;"
            //name="partyContact"
            //pattern="^\d{10}$"
            //pattern="\d*"
            max="10"
            placeholder="Enter 10 digit Phone no"
            autoComplete="off"
            onChange={this.onChange}
            required
          />
      </Fragment>
    );
  }
}
